mapper.select:
SELECT

FROM

WHERE
  ah.action_type = #{actionType}
  AND ah.content_type = #{contentType}
  AND ah.content_id = #{contentId}
  AND ah.user_id = #{userId}

mapper.delete:
DELETE FROM
  ACTION_HISTORY
WHERE
  action_type = #{actionType}
  AND content_type = #{contentType}
  AND content_id = #{contentId}
  AND user_id = #{userId}
