mapper.select:
SELECT

FROM

WHERE
  cmt.status = 'SERVICE'
  AND cmt.content_type = #{contentType}
  AND cmt.content_id = #{contentId}
  AND cmt.id < #{lastId}
ORDER BY
  cmt.id DESC
LIMIT
  #{size}

SELECT
FROM
WHERE cmt.status = 'SERVICE' AND cmt.id = #{id}

mapper.update:
UPDATE COMMENT SET
body = #{body},
up_dttm = NOW() WHERE id = #{id}

UPDATE COMMENT
SET status = #{status}, up_dttm = NOW()
WHERE id = #{id}

mapper.select:
SELECT count
FROM COMMENT_META
WHERE
  status = 'SERVICE'
  AND content_type = #{contentType}
  AND content_id = #{contentId}

mapper.update:
UPDATE COMMENT_META
SET count = count + #{operand}
WHERE
  content_type = #{contentType}
  AND content_id = #{contentId}

UPDATE COMMENT_META
SET status = #{status}, up_dttm = NOW()
WHERE
  content_type = #{contentType}
  AND content_id = #{contentId}
